// src/App.js
import React from 'react';
import { Button, Navbar, Container, Nav } from 'react-bootstrap';

function App() {
  return (
    <div dir="rtl"> {/* Set Right-to-Left for the entire app */}
      <Navbar bg="dark" variant="dark">
        <Container>
          <Navbar.Brand href="#home">تطبيق المدرسة</Navbar.Brand>
          <Nav className="me-auto">
            <Nav.Link href="#home">الصفحة الرئيسية</Nav.Link>
            <Nav.Link href="#teachers">المعلمين</Nav.Link>
            <Nav.Link href="#students">الطلاب</Nav.Link>
          </Nav>
        </Container>
      </Navbar>

      <div className="text-center mt-5">
        <h1>مرحبًا بكم في مشروع المدرسة</h1>
        <Button variant="primary" size="lg">
          ابدأ الآن
        </Button>
      </div>
    </div>
  );
}

export default App;
