import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Button, Form, Modal } from 'react-bootstrap';

const EnseignantForm = ({ enseignant, onClose }) => {
    const [formData, setFormData] = useState({
        nom: '',
        prenom: '',
        email: '',
        telephone: '',
        matiere: '',
        adresse: '',
        sexe: '',
        dateNaissance: '',
        salaire: ''
    });

    const [show, setShow] = useState(false);

    useEffect(() => {
        if (enseignant) {
            setFormData(enseignant);
            setShow(true);
        } else {
            setFormData({
                nom: '',
                prenom: '',
                email: '',
                telephone: '',
                matiere: '',
                adresse: '',
                sexe: '',
                dateNaissance: '',
                salaire: ''
            });
            setShow(false);
        }
    }, [enseignant]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (enseignant) {
            await axios.put(`/api/enseignants/${enseignant.id}`, formData);
        } else {
            await axios.post('/api/enseignants', formData);
        }
        onClose();
    };

    return (
        <>
            <Button variant="primary" onClick={() => setShow(true)}>
                Ajouter Enseignant
            </Button>

            <Modal show={show} onHide={onClose}>
                <Modal.Header closeButton>
                    <Modal.Title>{enseignant ? 'Modifier Enseignant' : 'Ajouter Enseignant'}</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form onSubmit={handleSubmit}>
                        {Object.keys(formData).map((key) => (
                            <Form.Group key={key} controlId={key}>
                                <Form.Label>{key.charAt(0).toUpperCase() + key.slice(1)}</Form.Label>
                                <Form.Control
                                    type="text"
                                    name={key}
                                    value={formData[key]}
                                    onChange={handleChange}
                                    required
                                />
                            </Form.Group>
                        ))}
                        <Button variant="primary" type="submit">
                            {enseignant ? 'Modifier' : 'Ajouter'}
                        </Button>
                    </Form>
                </Modal.Body>
            </Modal>
        </>
    );
};

export default EnseignantForm;
